package cn.medemede.leecode.subsolutions;public class MaxSubArray {    /**     * 连续最大和     *     * @param nums     * @return     */    public int maxSubArray(int[] nums) {        if (nums.length == 0) {            return 0;        }        int max = nums[0];        for (int i = 1; i < nums.length; i++) {            if (nums[i - 1] > 0) {                nums[i] += nums[i - 1];            }            max = Math.max(max, nums[i]);        }        return max;    }}